import React, { useState, useContext, useEffect } from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const Booking = ({ doctor, avgRating }) => {
  const { _id, fees, reviews, name, available } = doctor;
  const navigate = useNavigate();

  const { user } = useContext(AuthContext);
  const serviceFee = 10;
  const totalAmount = Number(fees) + Number(serviceFee);

  const [booking, setBooking] = useState({
    userId: user && user._id,
    userEmail: user && user.email,
    doctorId: _id,
    doctorName: name,
    fullName: "",
    phone: "",
    dateOfBirth: "",
    sex: "",
    healthNumber: "",
    address: "",
    fees: totalAmount,
    bookAt: "",
    available: available,
    forMe: true,
    relationship: "",
  });

  useEffect(() => {
    const bookingTime = new Date();
    if (available) {
      const formattedBookingTime = bookingTime.toISOString().slice(0, 10); // Format to YYYY-MM-DD
      setBooking((prev) => ({ ...prev, bookAt: formattedBookingTime }));
    } else {
      bookingTime.setDate(bookingTime.getDate() + 1); // Book appointment after 1 day
      const formattedBookingTime = bookingTime.toISOString().slice(0, 10); // Format to YYYY-MM-DD
      setBooking((prev) => ({ ...prev, bookAt: formattedBookingTime }));
    }
  }, [available]); // runs whenever `available` changes

  const handleChange = (e) => {
    setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const handleClick = async (e) => {
    e.preventDefault();
    console.log(booking);

    // Basic validation
    if (
      !booking.fullName ||
      !booking.phone ||
      !booking.address ||
      !booking.dateOfBirth ||
      !booking.healthNumber
    ) {
      alert("Please fill in all required fields.");
      return;
    }

    try {
      if (!user || user === undefined || user === null) {
        return alert("Please sign in");
      }

      const res = await fetch(`${BASE_URL}/booking`, {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(booking),
      });

      const result = await res.json();

      if (!res.ok) {
        return alert(result.message);
      }
      navigate("/thank-you");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${fees} <span>.</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i
            className="ri-star-fill"
            style={{ color: "var(--secondary-color)" }}
          ></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>

      {/* =============== BOOKING FORM START ============== */}
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder="Full Name"
              id="fullName"
              required
              pattern="[^0-9]+" // Allow only non-numeric characters
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="tel"
              placeholder="Phone"
              id="phone"
              required
              pattern="[0-9]{10}" // Pattern for 10-digit phone number
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="date"
              placeholder="Date of Birth (YYYY-MM-DD)"
              id="dateOfBirth"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <select id="sex" onChange={handleChange}>
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </FormGroup>
          <FormGroup>
            <select id="healthNumber" onChange={handleChange}>
              <option value="">Select Health Number</option>
              {[...Array(10)].map((_, index) => (
                <option key={index + 1} value={index + 1}>
                  {index + 1}
                </option>
              ))}
            </select>
          </FormGroup>

          <FormGroup>
            <input
              type="text"
              placeholder="Address"
              id="address"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="date"
              placeholder="Book At"
              id="bookAt"
              disabled
              value={booking.bookAt} // Set to the calculated time
              onChange={handleChange}
            />
          </FormGroup>
          <div className="radio-labels">
            {" "}
            {/* Added */}
            <label>
              For me:
              <input
                type="radio"
                name="forMe"
                value="true"
                checked={booking.forMe}
                onChange={() =>
                  setBooking((prev) => ({ ...prev, forMe: true }))
                }
              />
            </label>
            <label>
              For my child(ren):
              <input
                type="radio"
                name="forMe"
                value="false"
                checked={!booking.forMe}
                onChange={() =>
                  setBooking((prev) => ({ ...prev, forMe: false }))
                }
              />
            </label>
          </div>
          {/* Additional fields for child(ren) */}
          {!booking.forMe && (
            <FormGroup>
              <select id="relationship" onChange={handleChange}>
                <option value="">Select Relationship</option>
                <option value="parent">Parent</option>
                <option value="guardian">Guardian</option>
                <option value="other">Other</option>
              </select>
            </FormGroup>
          )}
          {/* Additional fields can be added here */}
          <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>
            Book Now
          </Button>
        </Form>
      </div>
      {/* =============== BOOKING FORM END ================ */}

      {/* =============== BOOKING BOTTOM ================ */}
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ${fees} <i className="ri-close-line"></i> 1 person
            </h5>
            <span>${fees}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service charge</h5>
            <span>${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>${totalAmount}</span>
          </ListGroupItem>
        </ListGroup>
        <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>
          Book Now
        </Button>
      </div>
      {/* =============== BOOKING BOTTOM END ================ */}
    </div>
  );
};

export default Booking;
