import React, { useState } from "react";
import { Card, CardBody } from "reactstrap";
import { Link } from "react-router-dom";
import "./doctor-card.css";
import calculateAvgRating from "../utils/avgRating";

const DoctorCard = ({ doctor }) => {
  const { _id, name, hospital, image, fees, available, reviews } = doctor;

  const { totalRating, avgRating } = calculateAvgRating(reviews);

  const [isFlipped, setIsFlipped] = useState(false);

  const handleCardClick = () => {
    setIsFlipped(!isFlipped);
  };

  return (
    <div className="tour__card" onClick={handleCardClick}>
      <Card className={isFlipped ? "flipped" : ""}>
        <div className="card__front">
          <div className="tour__img">
            <img src={image} alt="doctor-img" />
            {available && <span>Available</span>}
          </div>

          <CardBody>
            <div className="card__top d-flex align-items-center justify-content-between">
              <span className="tour__location d-flex align-items-center gap-1">
                <i class="ri-map-pin-line"></i> {hospital}
              </span>
              <span className="tour__rating d-flex align-items-center gap-1">
                <i class="ri-star-fill"></i>{" "}
                {avgRating === 0 ? null : avgRating}
                {totalRating === 0 ? (
                  "Not rated"
                ) : (
                  <span>({reviews.length})</span>
                )}
              </span>
            </div>

            <h5 className="tour__title">
              <Link to={`/doctors/${_id}`}>{name}</Link>
            </h5>

            <div className="card__bottom d-flex align-items-center justify-content-between mt-3">
              <h5>
                ${fees} <span> /per person</span>
              </h5>
            </div>
          </CardBody>
        </div>

        <div className="card__back">
          <button className="booking__btn" onClick={(e) => e.stopPropagation()}>
            Book Now
          </button>
        </div>
      </Card>
    </div>
  );
};

export default DoctorCard;
