import React, { useState, useContext, useEffect } from "react";
import "./booking.css";
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from "reactstrap";

import { useNavigate } from "react-router-dom";
import { AuthContext } from "../../context/AuthContext";
import { BASE_URL } from "../../utils/config";

const Booking = ({ doctor, avgRating }) => {
  const { _id, fees, reviews, name } = doctor;
  const navigate = useNavigate();

  const { user } = useContext(AuthContext);

  const [booking, setBooking] = useState({
    userId: user && user._id,
    userEmail: user && user.email,
    doctorId: _id,
    doctorName: name,
    fullName: "",
    phone: "",
    dateOfBirth: "",
    sex: "",
    healthNumber: "",
    address: "",
    fees: fees,
    bookAt: "",
  });

  useEffect(() => {
    const bookingTime = new Date();
    bookingTime.setMinutes(bookingTime.getMinutes() + 10); // Add 10 minutes
    const formattedBookingTime = bookingTime.toISOString().slice(0, 16); // Format to YYYY-MM-DDTHH:mm
    setBooking((prev) => ({ ...prev, bookAt: formattedBookingTime }));
  }, []); // Run only once when component mounts

  const handleChange = (e) => {
    setBooking((prev) => ({ ...prev, [e.target.id]: e.target.value }));
  };

  const serviceFee = 10;
  const totalAmount = Number(fees) + Number(serviceFee);

  const handleClick = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!booking.fullName || !booking.phone || !booking.bookAt) {
      alert("Please fill in all required fields.");
      return;
    }

    try {
      if (!user || user === undefined || user === null) {
        return alert("Please sign in");
      }

      const res = await fetch(`${BASE_URL}/booking`, {
        method: "post",
        headers: {
          "content-type": "application/json",
        },
        credentials: "include",
        body: JSON.stringify(booking),
      });

      const result = await res.json();

      if (!res.ok) {
        return alert(result.message);
      }
      navigate("/thank-you");
    } catch (error) {
      alert(error.message);
    }
  };

  return (
    <div className="booking">
      <div className="booking__top d-flex align-items-center justify-content-between">
        <h3>
          ${fees} <span>.</span>
        </h3>
        <span className="tour__rating d-flex align-items-center">
          <i
            class="ri-star-fill"
            style={{ color: "var(--secondary-color)" }}
          ></i>
          {avgRating === 0 ? null : avgRating} ({reviews?.length})
        </span>
      </div>

      {/* =============== BOOKING FORM START ============== */}
      <div className="booking__form">
        <h5>Information</h5>
        <Form className="booking__info-form" onSubmit={handleClick}>
          <FormGroup>
            <input
              type="text"
              placeholder="Full Name"
              id="fullName"
              required
              pattern="![0-9]"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="tel"
              placeholder="Phone"
              id="phone"
              required
              pattern="[0-9]{10}" // Pattern for 10-digit phone number
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="date"
              placeholder="Date of Birth"
              id="dateOfBirth"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <select id="sex" onChange={handleChange}>
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
          </FormGroup>
          <FormGroup>
            <input
              type="text"
              placeholder="Health Number"
              id="healthNumber"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="text"
              placeholder="Address"
              id="address"
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <input
              type="date"
              placeholder="Book At"
              id="bookAt"
              disabled
              value={booking.bookAt} // Set to the calculated time
              onChange={handleChange}
            />
          </FormGroup>
          {/* Additional fields can be added here */}
          <Button className="btn primary__btn w-100 mt-4" type="submit">
            Book Now
          </Button>
        </Form>
      </div>
      {/* =============== BOOKING FORM END ================ */}

      {/* =============== BOOKING BOTTOM ================ */}
      <div className="booking__bottom">
        <ListGroup>
          <ListGroupItem className="border-0 px-0">
            <h5 className="d-flex align-items-center gap-1">
              ${fees} <i class="ri-close-line"></i> 1 person
            </h5>
            <span>${fees}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0">
            <h5>Service charge</h5>
            <span>${serviceFee}</span>
          </ListGroupItem>
          <ListGroupItem className="border-0 px-0 total">
            <h5>Total</h5>
            <span>${totalAmount}</span>
          </ListGroupItem>
        </ListGroup>
      </div>
      {/* =============== BOOKING BOTTOM END ================ */}
    </div>
  );
};

export default Booking;
