import React, { useState } from "react";
import "./newsletter.css";
import { Container, Row, Col } from "reactstrap";
import maleTourist from "../assets/images/male-tourist.png";

const NewsLetter = () => {
  const [email, setEmail] = useState("");

  const handleSubscribe = () => {
    alert("Subscribed successfully!");
    setEmail("");
  };

  const handleInputChange = (event) => {
    setEmail(event.target.value);
  };

  return (
    <section className="newsletter">
      <Container>
        <Row>
          <Col lg="6">
            <div className="newsletter__content">
              <h2>
                "Subscribe now to receive valuable appointment-related updates
                and essential healthcare information straight to your inbox!"
              </h2>

              <div className="newsletter__input">
                <input
                  type="email"
                  placeholder="Enter your email"
                  value={email}
                  onChange={handleInputChange}
                />
                <button
                  className="btn newsletter__btn"
                  onClick={handleSubscribe}
                >
                  Subscribe
                </button>
              </div>
              <p>
                Subscribe now to receive invaluable travel insights straight to
                your inbox! From hidden gems to expert tips, embark on your next
                adventure fully prepared!
              </p>
            </div>
          </Col>
          <Col lg="6">
            <div className="newsletter__img">
              <img src={maleTourist} alt="" />
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
};

export default NewsLetter;
